import React from 'react';
import avatar from "../images/avatar.png";
import api from '../utils/api';
import Card from "./Card";
import {CurrentUserContext} from "../contexts/CurrentUserContext";

function Main({onEditAvatar, onEditProfile, onAddPlace, onCardClick}) {

    const [userDescription, setUserDescription] = React.useState('Исследователь океана');
    const [userAvatar, setUserAvatar] = React.useState(avatar);
    const [cards, setCards] = React.useState([]);

    const currentUser = React.useContext(CurrentUserContext);

    React.useEffect(() => {
        Promise.all([api.getInitialCards(), api.getUserInfo()]).then(([initialCards, userInfo]) => {
            setUserDescription(userInfo.about);
            setUserAvatar(userInfo.avatar);
            setCards(initialCards);
        }).catch((err) => {
            console.log(err);
        })
    }, []);

    return (
        <main>
            <section className="profile">
                <div className="profile__info-row">
                    <div className="profile__image-container" onClick={onEditAvatar}>
                        <img className="profile__image" alt="Аватар"
                             src={userAvatar}/>
                    </div>
                    <div className="profile__info-column">
                        <div className="profile__info-top-row">
                            <h1 className="profile__info-name">{currentUser.name}</h1>
                            <button type="button" className="profile__edit-button" onClick={onEditProfile}>
                            </button>
                        </div>
                        <p className="profile__info-description">{userDescription}</p>
                    </div>
                </div>
                <button type="button" className="profile__add-button" onClick={onAddPlace}/>
            </section>
            <section className="elements">
                {(cards.length === 0) ? (<p className="elements__no-items">Добавьте своё первое место!</p>) : (
                    cards.map((card, i) => {
                        return (
                            <Card card={card} onCardClick={onCardClick} key={i}/>
                        );
                    })
                )}
            </section>
        </main>
    );
}

export default Main;
