import React from 'react';

function PopupWithForm(props) {

    React.useEffect(() => {
        document.addEventListener('keydown', handleEsc)
    })

    return (
        <div className={props.isOpen ? `popup popup_${props.name} popup_opened` :`popup popup_${props.name}`}>
            <section className="popup__container">
                <button className="popup__close-button" type="button" onClick={props.onClose}/>
                <form className="form form_type_edit" name={`form-on-popup-${props.name}`} noValidate>
                    <h3 className="form__title">{props.title}</h3>
                    {props.children}
                    <button type="submit" className="form__submit-button">Сохранить</button>
                </form>
            </section>
        </div>
    );
}

export default PopupWithForm;
