import React from 'react';
import avatar from "../images/avatar.png";
import api from '../utils/api';
import Card from "./Card";

function Main(props) {

    const [userName, setUserName] = React.useState('Жак-Ив Кусто');
    const [userDescription, setUserDescription] = React.useState('Исследователь океана');
    const [userAvatar, setUserAvatar] = React.useState(avatar);
    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {

        const initialInfoPromises = Promise.all([api.getInitialCards(), api.getUserInfo()]);
        setUserName(initialInfoPromises[0])
        /*api.getUserInfo().then((res) => {
            setUserName(res.name);
            setUserDescription(res.about);
            setUserAvatar(res.avatar);
        });*/

       // api.getInitialCards().then((res) => {
            //setCards(res[0]);
         // })
    });

    return (
        <main>
            <section className="profile">
                <div className="profile__info-row">
                    <div className="profile__image-container" onClick={props.onEditAvatar}>
                        <img className="profile__image" alt="Аватар"
                             src={userAvatar}/>
                    </div>
                    <div className="profile__info-column">
                        <div className="profile__info-top-row">
                            <h1 className="profile__info-name">{userName}</h1>
                            <button type="button" className="profile__edit-button" onClick={props.onEditAvatar}>
                            </button>
                        </div>
                        <p className="profile__info-description">{userDescription}</p>
                    </div>
                </div>
                <button type="button" className="profile__add-button" onClick={props.onAddPlace}/>
            </section>
            <section className="elements">
                {(cards.length === 0) ? (<p className="elements__no-items">Добавьте своё первое место!</p>) : (
                    cards.map((card, i) => {
                        return (
                            <Card card={card} onCardClick={props.onCardClick} id={i}/>
                        );
                    })
                )}
            </section>
        </main>
    );
}

export default Main;
