import React from 'react';
import avatar from "../images/avatar.png";

function f(){
    document.querySelector('.popup_change_personal-info').classList.add('popup_is-opened')
}

function Main(){
    return (
        <main>
            <section className="profile">
                <div className="profile__info-row">
                    <div className="profile__image-container">
                        <img className="profile__image" alt="Аватар"
                             src={avatar}/>
                    </div>
                    <div className="profile__info-column">
                        <div className="profile__info-top-row">
                            <h1 className="profile__info-name">Жак-Ив Кусто</h1>
                            <button type="button" className="profile__edit-button" onClick={f()}>
                            </button>
                        </div>
                        <p className="profile__info-description">Исследователь океана</p>
                    </div>
                </div>
                <button type="button" className="profile__add-button"/>
            </section>
            <section className="elements">
                <p className="elements__no-items">Добавьте своё первое место!</p>
            </section>
        </main>
    );
}

export default Main;
