import React from "react";
import {Redirect, Route, Switch, withRouter} from 'react-router-dom';
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import api from "../utils/api";
import {CurrentUserContext} from "../contexts/CurrentUserContext";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import ProtectedRoute from "./ProtectedRoute";
import Register from "./Register";
import Login from "./Login";
import RegisterButton from "./RegisterButton";
import LoginButton from "./LoginButton";
import {authorize, checkTokenValidity, register} from "../utils/authorization";
import AuthorizedHeader from "./AuthorizedHeader";
import InfoPopup from "./InfoPopup";
import stateOkImg from "../images/ok.svg"
import stateErrorImg from "../images/not_ok.svg"

function App(props) {
    const [isEditProfilePopupOpen, setEditProfilePopupState] = React.useState(false);
    const [isAddPlacePopupOpen, setAddPlacePopupState] = React.useState(false);
    const [isEditAvatarPopupOpen, setEditAvatarPopupState] = React.useState(false);
    const [isInfoPopupOpen, setInfoPopupState] = React.useState(false);

    const [selectedCard, setSelectedCard] = React.useState(null);
    const [cards, setCards] = React.useState([]);

    const [currentUser, setCurrentUser] = React.useState(null);
    const [email, setEmail] = React.useState(null);

    const [isLoggedIn, setIsLoggedIn] = React.useState(false); // признак авторизованности пользователя

    const {history} = props;

    React.useEffect(() => {
        Promise.all([api.getInitialCards(), api.getUserInfo()]).then(([initialCards, userInfo]) => {
            setCurrentUser(userInfo);
            setCards(initialCards);
        }).catch((err) => {
            console.log(err);
        })
    }, []);

    /*React.useEffect(() => {
        if (isLoggedIn) {
            history.push("/")
        }
    }, [isLoggedIn]);*/

    React.useEffect(() => {
        checkToken();
    }, []);

    function handleCardLike(card) {
        const isLiked = card.likes.some(i => i._id === currentUser._id);
        if (!isLiked) {
            // Отправляем запрос в API и получаем обновлённые данные карточки
            api.addLike(card._id).then((newCard) => {
                setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
            }).catch((err) => {
                console.log(err);
            });
        } else {
            api.removeLike(card._id).then((newCard) => {
                setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
            }).catch((err) => {
                console.log(err);
            });
        }
    }

    function handleCardDelete(card) {
        api.removeCard(card._id).then(() => {
            setCards((state) => state.filter((c) => c._id !== card._id));
        }).catch((err) => {
            console.log(err);
        });
    }

    function handleEditAvatarClick() {
        setEditAvatarPopupState(true);
    }

    function handleEditProfileClick() {
        setEditProfilePopupState(true);
    }

    function handleAddPlaceClick() {
        setAddPlacePopupState(true);
    }

    function handleCardClick(card) {
        setSelectedCard(card);
    }

    function closeAllPopups() {
        setEditAvatarPopupState(false);
        setEditProfilePopupState(false);
        setAddPlacePopupState(false);
        setSelectedCard(null);
    }

    function handleUpdateUser({name, about}) {
        api.updateUserInfo(name, about).then((info) => {
            setCurrentUser(info);
            closeAllPopups();
        }).catch((err) => {
            console.log(err);
        });
    }

    function handleUpdateAvatar({url}) {
        api.changeAvatar(url).then((info) => {
            setCurrentUser(info);
            closeAllPopups();
        }).catch((err) => {
            console.log(err);
        })
    }

    function handleAddPlaceSubmit({name, url}) {
        api.addCard(name, url).then((item) => {
            setCards([item, ...cards]);
            closeAllPopups();
        }).catch((err) => {
            console.log(err);
        })
    }

    function handleRegister(password, email) {
        register(password, email).then(() => {
            setIsLoggedIn(true);
            return true;
            // setInfoPopupState(true);
            // history.push("/");
        }).catch(() => {return false;});
    }

    function handleAuthorization(password, email) {
        authorize(password, email).then((res) => {
            setIsLoggedIn(true);
            localStorage.setItem('token', res.token);
            renderPopup(true);
            //setInfoPopupState(true);
            // history.push("/");
        }).catch(() => {return false;})
    }

    function checkToken() {
        const token = localStorage.getItem('token');
        if (token) {
            checkTokenValidity(token).then((res) => {
                setEmail(res.data.email);
                setIsLoggedIn(true);
                history.push("/");
            }).catch(() => {
                setIsLoggedIn(false);
                history.push("/sign-in");
            })
        }
    }

    function handleClick(path) {
        history.push(`${path}`);
    }

    function handleLogout(path){
        handleClick(path);
        setIsLoggedIn(false);
        localStorage.removeItem('token');
    }

    function renderPopup(state){
        if(state === true){
            return (<InfoPopup isOpen={isInfoPopupOpen} src={stateOkImg} text="Вы успешно зарегистрировались!" name="info-popup"/>);
        } else {
            return (<InfoPopup isOpen={isInfoPopupOpen} src={stateErrorImg} text="Что-то пошло не так! Попробуйте ещё раз."/>);
        }
    }

    return (
        <CurrentUserContext.Provider value={currentUser}>
            <div className="root">
                <div className="root__container">
                    <Switch>
                        <Route exact path="/sign-up">
                            <Header/>
                            <RegisterButton path="/sign-in" onClick={handleClick}/>
                            <Register onHandleSubmit={handleRegister}/>
                            {renderPopup}
                        </Route>
                        <Route exact path="/sign-in">
                            <Header/>
                            <LoginButton path="/sign-up" onClick={handleClick}/>
                            <Login onHandleSubmit={handleAuthorization}/>
                            <InfoPopup isOpen={isInfoPopupOpen} src={stateOkImg} text="Вы успешно зарегистрировались!" name="info-popup"/>
                        </Route>
                        <ProtectedRoute
                            path="/"
                            loggedIn={isLoggedIn}>
                            <AuthorizedHeader email={email} path={"/sign-in"} onClick={handleLogout}/>
                            <Main
                                onEditProfile={handleEditProfileClick}
                                onAddPlace={handleAddPlaceClick}
                                onEditAvatar={handleEditAvatarClick}
                                onCardClick={handleCardClick}
                                cards={cards}
                                onCardLike={handleCardLike}
                                onCardDelete={handleCardDelete}
                            />
                        </ProtectedRoute>
                        <Route exact path="/">
                            {isLoggedIn ? <Redirect to="/"/> : <Redirect to="/sign-in"/>}
                        </Route>
                    </Switch>
                    <Footer/>
                    <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups}
                                      onUpdateUser={handleUpdateUser} submitBtnName="Сохранить"/>

                    <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups}
                                   onAddPlace={handleAddPlaceSubmit} submitBtnName="Создать"/>

                    <PopupWithForm title="Вы уверены?" name="are_you-sure" onClose={closeAllPopups}
                                   submitBtnName="Сохранить"/>

                    <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups}
                                     onUpdateAvatar={handleUpdateAvatar} submitBtnName="Сохранить"/>

                    <ImagePopup card={selectedCard} onClose={closeAllPopups}/>
                </div>
            </div>
        </CurrentUserContext.Provider>
    );
}

export default withRouter(App);
