import React from 'react';

function ImagePopup({onClose, isOpen, card}) {
    function handleEscClick(evt) {
        if (evt.key === 'Escape') {
            onClose();
        }
    }

    function handleMouseDown(evt){
        if (evt.target.classList.contains('popup_opened')) {
            onClose();
        }
    }

    React.useEffect(() => {
        document.addEventListener('keydown', handleEscClick);

        return () => {
            document.removeEventListener('keydown', handleEscClick);
        }
    }, [])

    return (
        <div className={isOpen ? `popup popup_with_image popup_opened` : `popup popup_with_image`} onMouseDown={onClose}>
            <section className="popup__container">
                <button className="popup__close-button" type="button" onClick={onClose}/>
                <img className="popup__image" src={(card === null) ? "#" : card.link} alt="Фото места"/>
                <p className="popup__image-description">{(card === null) ? "" : card.name}</p>
            </section>
        </div>
    );
}

export default ImagePopup;
