import React from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import avatar from "../images/avatar.png";
import api from "../utils/api";

function App() {
    const [isEditProfilePopupOpen, setEditProfilePopupState] = React.useState(false);
    const [isAddPlacePopupOpen, setAddPlacePopupState] = React.useState(false);
    const [isEditAvatarPopupOpen, setEditAvatarPopupState] = React.useState(false);
    const [isImagePopupOpen, setImagePopupState] = React.useState(false);
    const [selectedCard, setSelectedCard] = React.useState(null);

    const [currentUser, setCurrentUser] = React.useState(null);

    React.useEffect(() => {
        api.getUserInfo().then(( userInfo) => {
            setCurrentUser(userInfo._id);
        }).catch((err) => {
            console.log(err);
        })
    }, []);

    function handleEditAvatarClick() {
        setEditAvatarPopupState(true);
    }

    function handleEditProfileClick() {
        setEditProfilePopupState(true);
    }

    function handleAddPlaceClick() {
        setAddPlacePopupState(true);
    }

    function handleCardClick(card) {
        setSelectedCard(card);
        setImagePopupState(true);
    }

    function closeAllPopups(evt) {
        setEditAvatarPopupState(false);
        setEditProfilePopupState(false);
        setAddPlacePopupState(false);
        setImagePopupState(false);
        setSelectedCard(null);
    }

    return (
        <div className="root">
            <div className="root__container">
                <Header/>
                <Main
                    onEditProfile={handleEditProfileClick}
                    onAddPlace={handleAddPlaceClick}
                    onEditAvatar={handleEditAvatarClick}
                    onCardClick={handleCardClick}
                />
                <Footer/>

                <PopupWithForm
                    title="Редактировать профиль"
                    name={"change_personal-info"}
                    isOpen={isEditProfilePopupOpen}
                    onClose={closeAllPopups}>
                    <input
                        type="text"
                        id="name"
                        className="form__item"
                        name="name-input"
                        minLength="2"
                        maxLength="40"
                        required/>
                    <span className="form__input-error name-input-error" id='nameInputError'/>
                    <input
                        type="text"
                        id="additionalInfo"
                        className="form__item"
                        name="additional-info-input"
                        minLength="2"
                        maxLength="200"
                        required/>
                    <span className="form__input-error additional-info-input-error" id='additionalInfoInputError'/>
                </PopupWithForm>

                <PopupWithForm
                    title="Новое место"
                    name="add_new-place"
                    isOpen={isAddPlacePopupOpen}
                    onClose={closeAllPopups}>
                    <input
                        type="text"
                        id="place"
                        className="form__item"
                        name="place-input"
                        placeholder="Название"
                        minLength="2" maxLength="30" required/>
                    <span className="form__input-error place-input-error" id='placeInputError'/>
                    <input
                        type="url"
                        id="imageUrl"
                        className="form__item"
                        name="image-url-input"
                        placeholder="Ссылка на картинку"
                        required/>
                    <span className="form__input-error image-url-input-error" id='imageUrlInputError'/>
                </PopupWithForm>

                <PopupWithForm title="Вы уверены?" name="are_you-sure" onClose={closeAllPopups}/>

                <PopupWithForm
                    title="Обновить аватар"
                    name="change-profile-image"
                    isOpen={isEditAvatarPopupOpen}
                    onClose={closeAllPopups}>
                    <input
                        type="url"
                        id="profileImg"
                        className="form__item"
                        name="image-url-input"
                        placeholder="Ссылка на картинку"
                        required/>
                    <span className="form__input-error image-url-input-error" id='profileImgUrlInputError'/>
                </PopupWithForm>

                <ImagePopup card={selectedCard} onClose={closeAllPopups} isOpen={isImagePopupOpen}/>
            </div>
        </div>
    );
}

export default App;
