import React from 'react';

function Card(props) {

    function handleClick() {
        props.onCardClick(props.card);
    }

    return (
        <figure className="element" key={props.key}>
            <img className="element__image" src={props.card.link} alt="место" onClick={handleClick}/>
            <figcaption className="element__caption">
                <h3 className="element__caption-text">{props.card.name}</h3>
                <div className="element__heart-column">
                    <button type="button" className="element__heart"/>
                    <span className="element__heart-count">{(props.card.likes.length === 0) ? 0 : props.card.likes.length}</span>
                </div>
            </figcaption>
            <button className="element__delete-btn" type="button"/>
        </figure>
    );
}

export default Card;
