import React from 'react';

function PopupWithForm(props) {

    function handleEscClick(evt) {
        if (evt.key === 'Escape') {
            props.onClose();
        }
    }

    function handleMouseDown(evt){
        if (evt.target.classList.contains('popup_opened')) {
            props.onClose();
        }
    }

    React.useEffect(() => {
        document.addEventListener('keydown', handleEscClick.bind(this));

        return () => {
            document.removeEventListener('keydown', handleEscClick.bind(this));
        }
    })

    return (
        <div className={props.isOpen ? `popup popup_${props.name} popup_opened` : `popup popup_${props.name}`} onMouseDown={handleMouseDown}>
            <section className="popup__container">
                <button className="popup__close-button" type="button" onClick={props.onClose}/>
                <form className="form form_type_edit" name={`form-on-popup-${props.name}`} noValidate>
                    <h3 className="form__title">{props.title}</h3>
                    {props.children}
                    <button type="submit" className="form__submit-button">Сохранить</button>
                </form>
            </section>
        </div>
    );
}

export default PopupWithForm;
