import React from 'react';

function ImagePopup(props) {
    function handleEscClick(evt) {
        if (evt.key === 'Escape') {
            props.onClose();
        }
    }

    function handleMouseDown(evt){
        if (evt.target.classList.contains('popup_opened')) {
            props.onClose();
        }
    }

    React.useEffect(() => {
        document.addEventListener('keydown', handleEscClick);

        return () => {
            document.removeEventListener('keydown', handleEscClick);
        }
    }, [])

    return (
        <div className={props.isOpen ? `popup popup_with_image popup_opened` : `popup popup_with_image`} onMouseDown={handleMouseDown}>
            <section className="popup__container">
                <button className="popup__close-button" type="button" onClick={props.onClose}/>
                <img className="popup__image" src={props.card.link ?? "#"} alt="Фото места"/>
                <p className="popup__image-description">{props.card.name ?? "#"}</p>
            </section>
        </div>
    );
}

export default ImagePopup;
