import React from 'react';
import avatar from "../images/avatar.png";

function handleEditAvatarClick(){
    document.querySelector('.popup_change_personal-info').classList.add('popup_opened')
}

function handleEditProfileClick(){
    document.querySelector('.popup_change_personal-info').classList.add('popup_opened')
}

function handleAddPlaceClick(){
    document.querySelector('.popup_add_new-place').classList.add('popup_opened')
}

function Main(){
    return (
        <main>
            <section className="profile">
                <div className="profile__info-row">
                    <div className="profile__image-container" onClick={handleEditAvatarClick}>
                        <img className="profile__image" alt="Аватар"
                             src={avatar}/>
                    </div>
                    <div className="profile__info-column">
                        <div className="profile__info-top-row">
                            <h1 className="profile__info-name">Жак-Ив Кусто</h1>
                            <button type="button" className="profile__edit-button" onClick={handleEditProfileClick}>
                            </button>
                        </div>
                        <p className="profile__info-description">Исследователь океана</p>
                    </div>
                </div>
                <button type="button" className="profile__add-button" onClick={handleAddPlaceClick}/>
            </section>
            <section className="elements">
                <p className="elements__no-items">Добавьте своё первое место!</p>
            </section>
        </main>
    );
}

export default Main;
