import React from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import api from "../utils/api";
import {CurrentUserContext} from "../contexts/CurrentUserContext";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";

function App() {
    const [isEditProfilePopupOpen, setEditProfilePopupState] = React.useState(false);
    const [isAddPlacePopupOpen, setAddPlacePopupState] = React.useState(false);
    const [isEditAvatarPopupOpen, setEditAvatarPopupState] = React.useState(false);
    const [isImagePopupOpen, setImagePopupState] = React.useState(false);

    const [selectedCard, setSelectedCard] = React.useState(null);
    const [cards, setCards] = React.useState([]);

    const [currentUser, setCurrentUser] = React.useState(null);

    React.useEffect(() => {
        api.getUserInfo().then(( userInfo) => {
            setCurrentUser(userInfo);
        }).catch((err) => {
            console.log(err);
        })
    }, []);

    React.useEffect(() => {
        Promise.all([api.getInitialCards()]).then(([initialCards]) => {
            setCards(initialCards);
        }).catch((err) => {
            console.log(err);
        })
    }, []);

    function handleCardLike(card) {
        const isLiked = card.likes.some(i => i._id === currentUser._id);
        if(!isLiked){
            // Отправляем запрос в API и получаем обновлённые данные карточки
            api.addLike(card._id).then((newCard) => {
                setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
            });
        } else {
            api.removeLike(card._id).then((newCard) => {
                setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
            });
        }
    }

    function handleCardDelete(card){
        api.removeCard(card._id).then(() => {
            setCards((state) => state.filter((c) => c._id !== card._id));
        });
    }

    function handleEditAvatarClick() {
        setEditAvatarPopupState(true);
    }

    function handleEditProfileClick() {
        setEditProfilePopupState(true);
    }

    function handleAddPlaceClick() {
        setAddPlacePopupState(true);
    }

    function handleCardClick(card) {
        setSelectedCard(card);
        setImagePopupState(true);
    }

    function closeAllPopups() {
        setEditAvatarPopupState(false);
        setEditProfilePopupState(false);
        setAddPlacePopupState(false);
        setImagePopupState(false);
        setSelectedCard(null);
    }

    function handleUpdateUser({name, about}){
        api.updateUserInfo(name, about).then((info) => {
            setCurrentUser(info);
            closeAllPopups();
        });
    }

    function handleUpdateAvatar({url}){
        api.changeAvatar(url).then((info) => {
            setCurrentUser(info);
            closeAllPopups();
        })
    }

    return (
        <CurrentUserContext.Provider value={currentUser}>
        <div className="root">
            <div className="root__container">
                <Header/>
                <Main
                    onEditProfile={handleEditProfileClick}
                    onAddPlace={handleAddPlaceClick}
                    onEditAvatar={handleEditAvatarClick}
                    onCardClick={handleCardClick}
                    cards={cards}
                    onCardLike={handleCardLike}
                    onCardDelete={handleCardDelete}
                />
                <Footer/>

                <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser}/>

                <PopupWithForm
                    title="Новое место"
                    name="add_new-place"
                    isOpen={isAddPlacePopupOpen}
                    onClose={closeAllPopups}>
                    <input
                        type="text"
                        id="place"
                        className="form__item"
                        name="place-input"
                        placeholder="Название"
                        minLength="2" maxLength="30" required/>
                    <span className="form__input-error place-input-error" id='placeInputError'/>
                    <input
                        type="url"
                        id="imageUrl"
                        className="form__item"
                        name="image-url-input"
                        placeholder="Ссылка на картинку"
                        required/>
                    <span className="form__input-error image-url-input-error" id='imageUrlInputError'/>
                </PopupWithForm>

                <PopupWithForm title="Вы уверены?" name="are_you-sure" onClose={closeAllPopups}/>

                <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar}/>

                <ImagePopup card={selectedCard} onClose={closeAllPopups} isOpen={isImagePopupOpen}/>
            </div>
        </div>
        </CurrentUserContext.Provider>
    );
}

export default App;
