import React from 'react';
import PopupWithForm from "./PopupWithForm";

function EditProfilePopup({isOpen, onClose}) {

    const avatarRef = React.useRef(null);

    function handleChange() {
        console.log(avatarRef.current.value) // вызываем нужный метод на поле current объекта
    }

    return (
        <PopupWithForm
            title="Обновить аватар"
            name="change-profile-image"
            isOpen={isOpen}
            onClose={onClose}>
            <input
                type="url"
                id="profileImg"
                className="form__item"
                name="image-url-input"
                placeholder="Ссылка на картинку"
                ref={avatarRef}
                onChange={handleChange}
                required/>
            <span className="form__input-error image-url-input-error" id='profileImgUrlInputError'/>
        </PopupWithForm>
    );
}

export default EditProfilePopup;
